const BASE_URL = 'http://localhost:3000/api/traps';

export const trapApi = {
  getAllTraps: async () => {
    const response = await fetch(BASE_URL);
    return response.json();
  },

  createTrap: async (trapData) => {
    const response = await fetch(BASE_URL, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(trapData)
    });
    return response.json();
  },

  updateTrapByQR: async (qrCode, updates) => {
    const response = await fetch(`${BASE_URL}/qr/${qrCode}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updates)
    });
    return response.json();
  },

  deleteTrap: async (id) => {
    try {
      const response = await fetch(`${API_URL}/traps/${id}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Failed to delete trap');
      }

      return true;
    } catch (error) {
      console.error('Error deleting trap:', error);
      throw error;
    }
  },

  getTrapByQR: async (qrCode) => {
    const response = await fetch(`${BASE_URL}/qr/${qrCode}`);
    return response.json();
  }
};
